INSERT INTO `config_info` VALUES (553, 'erahub-base.yml', 'DEFAULT_GROUP', 'spring:\r\n  datasource:\r\n    dynamic:\r\n      # 设置默认的数据源或者数据源组,默认值即为 master\r\n      primary: master\r\n      datasource:\r\n        # 主库数据源\r\n        master:\r\n          driver-class-name: com.mysql.cj.jdbc.Driver\r\n          url: ${datasource.base-master.url}\r\n          username: ${datasource.base-master.username}\r\n          password: ${datasource.base-master.password}\r\n        # 从库数据源\r\n        # slave:\r\n        #  driver-class-name:\r\n        #  url:\r\n        #  username:\r\n        #  password:\r\n\r\n\r\n# 代码生成\r\ngen:\r\n  # 作者\r\n  author: erahub\r\n  # 默认生成包路径 base 需改成自己的模块名称 如 base monitor tool\r\n  packageName: com.erahub.base\r\n  # 自动去除表前缀，默认是false\r\n  autoRemovePre: false\r\n  # 表前缀（生成类名不会包含表前缀，多个用逗号分隔）\r\n  tablePrefix: sys_\r\n\r\n\r\nmail:\r\n  enabled: false\r\n  host: smtp.163.com\r\n  port: 465\r\n  # 是否需要用户名密码验证\r\n  auth: true\r\n  # 发送方，遵循RFC-822标准\r\n  from: xxx@163.com\r\n  # 用户名（注意：如果使用foxmail邮箱，此处user为qq号）\r\n  user: xxx@163.com\r\n  # 密码（注意，某些邮箱需要为SMTP服务单独设置密码，详情查看相关帮助）\r\n  pass: xxxxxxxxxx\r\n  # 使用 STARTTLS安全连接，STARTTLS是对纯文本通信协议的扩展。\r\n  starttlsEnable: true\r\n  # 使用SSL安全连接\r\n  sslEnable: true\r\n  # SMTP超时时长，单位毫秒，缺省值不超时\r\n  timeout: 0\r\n  # Socket连接超时值，单位毫秒，缺省值不超时\r\n  connectionTimeout: 0\r\n\r\nsms:\r\n  enabled: false\r\n  # 阿里云 dysmsapi.aliyuncs.com\r\n  # 腾讯云 sms.tencentcloudapi.com\r\n  endpoint: \"dysmsapi.aliyuncs.com\"\r\n  accessKeyId: xxxxxxx\r\n  accessKeySecret: xxxxxx\r\n  signName: 测试\r\n  # 腾讯专用\r\n  sdkAppId:\r\n', 'e8787f068b2f842b454155939e962d67', '2022-10-08 01:23:26', '2022-10-08 01:23:26', NULL, '183.209.136.16', '', 'prod', NULL, NULL, NULL, 'yaml', NULL, '');
