INSERT INTO `his_config_info` VALUES (168, 65, 'eapplication-common.yml', 'DEFAULT_GROUP', '', 'server:\r\n  # undertow 配置\r\n  undertow:\r\n    # HTTP post内容的最大大小。当值为-1时，默认值为大小是无限的\r\n    max-http-post-size: -1\r\n    # 以下的配置会影响buffer,这些buffer会用于服务器连接的IO操作,有点类似netty的池化内存管理\r\n    # 每块buffer的空间大小,越小的空间被利用越充分\r\n    buffer-size: 512\r\n    # 是否分配的直接内存\r\n    direct-buffers: true\r\n    threads:\r\n      # 设置IO线程数, 它主要执行非阻塞的任务,它们会负责多个连接, 默认设置每个CPU核心一个线程\r\n      io: 8\r\n      # 阻塞任务线程池, 当执行类似servlet请求阻塞操作, undertow会从这个线程池中取得线程,它的值设置取决于系统的负载\r\n      worker: 256\r\n\r\ndubbo:\r\n  application:\r\n    logger: slf4j\r\n    # 元数据中心 local 本地 remote 远程 这里使用远程便于其他服务获取\r\n    metadataType: remote\r\n    # 可选值 interface、instance、all，默认是 all，即接口级地址、应用级地址都注册\r\n    register-mode: instance\r\n    service-discovery:\r\n      # FORCE_INTERFACE，只消费接口级地址，如无地址则报错，单订阅 2.x 地址\r\n      # APPLICATION_FIRST，智能决策接口级/应用级地址，双订阅\r\n      # FORCE_APPLICATION，只消费应用级地址，如无地址则报错，单订阅 3.x 地址\r\n      migration: FORCE_APPLICATION\r\n  protocol:\r\n    # 设置为 tri 即可使用 Triple 3.0 新协议\r\n    # 性能对比 dubbo 协议并没有提升 但基于 http2 用于多语言异构等 http 交互场景\r\n    # 使用 dubbo 协议通信\r\n    name: dubbo\r\n    # dubbo 协议端口(-1表示自增端口,从20880开始)\r\n    port: -1\r\n    # 指定dubbo协议注册ip\r\n    # host: 192.168.0.100\r\n  # 注册中心配置\r\n  registry:\r\n    address: nacos://${spring.cloud.nacos.server-addr}\r\n    group: DUBBO_GROUP\r\n    parameters:\r\n      namespace: ${spring.profiles.active}\r\n  # 消费者相关配置\r\n  consumer:\r\n    # 结果缓存(LRU算法)\r\n    # 会有数据不一致问题 建议在注解局部开启\r\n    cache: false\r\n    # 支持校验注解\r\n    validation: true\r\n    # 超时时间\r\n    timeout: 3000\r\n    # 初始化检查\r\n    check: false\r\n  scan:\r\n    # 接口实现类扫描\r\n    base-packages: com.erahub.**.dubbo\r\n  # 自定义配置\r\n  custom:\r\n    # 全局请求log\r\n    request-log: true\r\n    # info 基础信息 param 参数信息 full 全部\r\n    log-level: info\r\n\r\nspring:\r\n  # 资源信息\r\n  messages:\r\n    # 国际化资源文件路径\r\n    basename: i18n/messages\r\n  servlet:\r\n    multipart:\r\n      # 整个请求大小限制\r\n      max-request-size: 20MB\r\n      # 上传单个文件大小限制\r\n      max-file-size: 10MB\r\n  #jackson配置\r\n  jackson:\r\n    # 日期格式化\r\n    date-format: yyyy-MM-dd HH:mm:ss\r\n    serialization:\r\n      # 格式化输出\r\n      INDENT_OUTPUT: false\r\n      # 忽略无法转换的对象\r\n      fail_on_empty_beans: false\r\n    deserialization:\r\n      # 允许对象忽略json中不存在的属性\r\n      fail_on_unknown_properties: false\r\n  cloud:\r\n    # sentinel 配置\r\n    sentinel:\r\n      # 取消控制台懒加载\r\n      eager: true\r\n      transport:\r\n        # 控制台地址\r\n        dashboard: localhost:8718\r\n\r\n  # redis通用配置 子服务可以自行配置进行覆盖\r\n  redis:\r\n    host: localhost\r\n    port: 6379\r\n    # 密码(如没有密码请注释掉)\r\n    #password:\r\n    database: 0\r\n    timeout: 10s\r\n    ssl: false\r\n\r\n# redisson 配置\r\nredisson:\r\n  # redis key前缀\r\n  keyPrefix:\r\n  # 线程池数量\r\n  threads: 4\r\n  # Netty线程池数量\r\n  nettyThreads: 8\r\n  # 单节点配置\r\n  singleServerConfig:\r\n    # 客户端名称\r\n    clientName: ${spring.application.name}\r\n    # 最小空闲连接数\r\n    connectionMinimumIdleSize: 8\r\n    # 连接池大小\r\n    connectionPoolSize: 32\r\n    # 连接空闲超时，单位：毫秒\r\n    idleConnectionTimeout: 10000\r\n    # 命令等待超时，单位：毫秒\r\n    timeout: 3000\r\n    # 发布和订阅连接池大小\r\n    subscriptionConnectionPoolSize: 50\r\n  # redisson 缓存配置\r\n  cacheGroup:\r\n    # 用例: @Cacheable(cacheNames=\"groupId\", key=\"#XXX\") 方可使用缓存组配置\r\n    - groupId: redissonCacheMap\r\n      # 组过期时间(脚本监控)\r\n      ttl: 60000\r\n      # 组最大空闲时间(脚本监控)\r\n      maxIdleTime: 60000\r\n      # 组最大长度\r\n      maxSize: 0\r\n\r\n# 分布式锁 lock4j 全局配置\r\nlock4j:\r\n  # 获取分布式锁超时时间，默认为 3000 毫秒\r\n  acquire-timeout: 3000\r\n  # 分布式锁的超时时间，默认为 30 毫秒\r\n  expire: 30000\r\n\r\n# 暴露监控端点\r\nmanagement:\r\n  endpoints:\r\n    web:\r\n      exposure:\r\n        include: \'*\'\r\n  endpoint:\r\n    health:\r\n      show-details: ALWAYS\r\n    logfile:\r\n      external-file: ./logs/${spring.application.name}/console.log\r\n\r\n# 日志配置\r\nlogging:\r\n  level:\r\n    org.springframework: warn\r\n    org.apache.dubbo: warn\r\n    com.alibaba.nacos: warn\r\n  config: classpath:logback.xml\r\n\r\n# Sa-Token配置\r\nsa-token:\r\n  # token名称 (同时也是cookie名称)\r\n  token-name: Authorization\r\n  # token有效期 设为一天 (必定过期) 单位: 秒\r\n  timeout: 86400\r\n  # token临时有效期 (指定时间无操作就过期) 单位: 秒\r\n  activity-timeout: 1800\r\n  # 开启内网服务调用鉴权\r\n  check-id-token: true\r\n  # Id-Token的有效期 (单位: 秒)\r\n  id-token-timeout: 600\r\n  # 是否允许同一账号并发登录 (为true时允许一起登录, 为false时新登录挤掉旧登录)\r\n  is-concurrent: true\r\n  # 在多人登录同一账号时，是否共用一个token (为true时所有登录共用一个token, 为false时每次登录新建一个token)\r\n  is-share: false\r\n  # 是否尝试从header里读取token\r\n  is-read-head: true\r\n  # 是否尝试从cookie里读取token\r\n  is-read-cookie: false\r\n  # token前缀\r\n  token-prefix: \"Bearer\"\r\n  # jwt秘钥\r\n  jwt-secret-key: abcdefghijklmnopqrstuvwxyz\r\n  # 是否输出操作日志\r\n  is-log: true\r\n\r\n# MyBatisPlus配置\r\n# https://baomidou.com/config/\r\nmybatis-plus:\r\n  # 不支持多包, 如有需要可在注解配置 或 提升扫包等级\r\n  # 例如 com.**.**.mapper\r\n  mapperPackage: com.erahub.**.mapper\r\n  # 对应的 XML 文件位置\r\n  mapperLocations: classpath*:mapper/**/*Mapper.xml\r\n  # 实体扫描，多个package用逗号或者分号分隔\r\n  typeAliasesPackage: com.erahub.**.domain\r\n  # 启动时是否检查 MyBatis XML 文件的存在，默认不检查\r\n  checkConfigLocation: false\r\n  configuration:\r\n    # 自动驼峰命名规则（camel case）映射\r\n    mapUnderscoreToCamelCase: true\r\n    # MyBatis 自动映射策略\r\n    # NONE：不启用 PARTIAL：只对非嵌套 resultMap 自动映射 FULL：对所有 resultMap 自动映射\r\n    autoMappingBehavior: PARTIAL\r\n    # MyBatis 自动映射时未知列或未知属性处理策\r\n    # NONE：不做处理 WARNING：打印相关警告 FAILING：抛出异常和详细信息\r\n    autoMappingUnknownColumnBehavior: NONE\r\n    # 更详细的日志输出 会有性能损耗 org.apache.ibatis.logging.stdout.StdOutImpl\r\n    # 关闭日志记录 (可单纯使用 p6spy 分析) org.apache.ibatis.logging.nologging.NoLoggingImpl\r\n    # 默认日志输出 org.apache.ibatis.logging.slf4j.Slf4jImpl\r\n    logImpl: org.apache.ibatis.logging.nologging.NoLoggingImpl\r\n  global-config:\r\n    # 是否打印 Logo banner\r\n    banner: true\r\n    dbConfig:\r\n      # 主键类型\r\n      # AUTO 自增 NONE 空 INPUT 用户输入 ASSIGN_ID 雪花 ASSIGN_UUID 唯一 UUID\r\n      idType: ASSIGN_ID\r\n      # 逻辑已删除值\r\n      logicDeleteValue: 2\r\n      # 逻辑未删除值\r\n      logicNotDeleteValue: 0\r\n      insertStrategy: NOT_NULL\r\n      updateStrategy: NOT_NULL\r\n      where-strategy: NOT_NULL\r\n\r\n# swagger配置\r\nswagger:\r\n  # 是否开启swagger\r\n  enabled: true\r\n  info:\r\n    # 标题\r\n    title: \'标题：erahub-Cloud-Plus微服务权限管理系统_接口文档\'\r\n    # 描述\r\n    description: \'描述：微服务权限管理系统, 具体包括XXX,XXX模块...\'\r\n    # 版本\r\n    version: \'版本号：系统版本...\'\r\n    # 作者信息\r\n    contact:\r\n      name: Lion Li\r\n      email: crazylionli@163.com\r\n      url: https://gitee.com/JavaLionLi/erahub-Cloud-Plus\r\n  components:\r\n    # 鉴权方式配置\r\n    security-schemes:\r\n      apiKey:\r\n        type: APIKEY\r\n        in: HEADER\r\n        name: ${sa-token.token-name}\r\n  # 服务文档路径映射 参考 gateway router 配置\r\n  # 默认为服务名去除前缀转换为path 此处填特殊的配置\r\n  service-mapping:\r\n    erahub-gen: /code\r\n\r\n# seata配置\r\nseata:\r\n  # 默认关闭，如需启用spring.datasource.dynami.seata需要同时开启\r\n  enabled: true\r\n  # Seata 应用编号，默认为 ${spring.application.name}\r\n  application-id: ${spring.application.name}\r\n  # Seata 事务组编号，用于 TC 集群名\r\n  tx-service-group: ${spring.application.name}-group\r\n  config:\r\n    type: nacos\r\n    nacos:\r\n      server-addr: ${spring.cloud.nacos.server-addr}\r\n      group: ${spring.cloud.nacos.config.group}\r\n      namespace: ${spring.profiles.active}\r\n      data-id: seata-server.properties\r\n  registry:\r\n    type: nacos\r\n    nacos:\r\n      application: erahub-seata-server\r\n      server-addr: ${spring.cloud.nacos.server-addr}\r\n      group: ${spring.cloud.nacos.discovery.group}\r\n      namespace: ${spring.profiles.active}\r\n', '5f60abc4eedd5124af3ecffd58941a21', '2022-09-12 08:35:09', '2022-09-12 00:35:09', NULL, '127.0.0.1', 'D', 'dev', '');
INSERT INTO `his_config_info` VALUES (1, 66, 'application-common.yml', 'DEFAULT_GROUP', '', 'server:\n  # undertow 配置\n  undertow:\n    # HTTP post内容的最大大小。当值为-1时，默认值为大小是无限的\n    max-http-post-size: -1\n    # 以下的配置会影响buffer,这些buffer会用于服务器连接的IO操作,有点类似netty的池化内存管理\n    # 每块buffer的空间大小,越小的空间被利用越充分\n    buffer-size: 512\n    # 是否分配的直接内存\n    direct-buffers: true\n    threads:\n      # 设置IO线程数, 它主要执行非阻塞的任务,它们会负责多个连接, 默认设置每个CPU核心一个线程\n      io: 8\n      # 阻塞任务线程池, 当执行类似servlet请求阻塞操作, undertow会从这个线程池中取得线程,它的值设置取决于系统的负载\n      worker: 256\n\ndubbo:\n  application:\n    logger: slf4j\n    # 元数据中心 local 本地 remote 远程 这里使用远程便于其他服务获取\n    metadataType: remote\n    # 可选值 interface、instance、all，默认是 all，即接口级地址、应用级地址都注册\n    register-mode: instance\n    service-discovery:\n      # FORCE_INTERFACE，只消费接口级地址，如无地址则报错，单订阅 2.x 地址\n      # APPLICATION_FIRST，智能决策接口级/应用级地址，双订阅\n      # FORCE_APPLICATION，只消费应用级地址，如无地址则报错，单订阅 3.x 地址\n      migration: FORCE_APPLICATION\n  protocol:\n    # 设置为 tri 即可使用 Triple 3.0 新协议\n    # 性能对比 dubbo 协议并没有提升 但基于 http2 用于多语言异构等 http 交互场景\n    # 使用 dubbo 协议通信\n    name: dubbo\n    # dubbo 协议端口(-1表示自增端口,从20880开始)\n    port: -1\n    # 指定dubbo协议注册ip\n    # host: 192.168.0.100\n  # 注册中心配置\n  registry:\n    address: nacos://${spring.cloud.nacos.server-addr}\n    group: DUBBO_GROUP\n    parameters:\n      namespace: ${spring.profiles.active}\n  # 消费者相关配置\n  consumer:\n    # 结果缓存(LRU算法)\n    # 会有数据不一致问题 建议在注解局部开启\n    cache: false\n    # 支持校验注解\n    validation: true\n    # 超时时间\n    timeout: 3000\n    # 初始化检查\n    check: false\n  scan:\n    # 接口实现类扫描\n    base-packages: com.erahub.**.dubbo\n  # 自定义配置\n  custom:\n    # 全局请求log\n    request-log: true\n    # info 基础信息 param 参数信息 full 全部\n    log-level: info\n\nspring:\n  # 资源信息\n  messages:\n    # 国际化资源文件路径\n    basename: i18n/messages\n  servlet:\n    multipart:\n      # 整个请求大小限制\n      max-request-size: 20MB\n      # 上传单个文件大小限制\n      max-file-size: 10MB\n  #jackson配置\n  jackson:\n    # 日期格式化\n    date-format: yyyy-MM-dd HH:mm:ss\n    serialization:\n      # 格式化输出\n      INDENT_OUTPUT: false\n      # 忽略无法转换的对象\n      fail_on_empty_beans: false\n    deserialization:\n      # 允许对象忽略json中不存在的属性\n      fail_on_unknown_properties: false\n  cloud:\n    # sentinel 配置\n    sentinel:\n      # 取消控制台懒加载\n      eager: true\n      transport:\n        # 控制台地址\n        dashboard: localhost:8718\n\n  # redis通用配置 子服务可以自行配置进行覆盖\n  redis:\n    host: localhost\n    port: 6379\n    # 密码(如没有密码请注释掉)\n    #password:\n    database: 0\n    timeout: 10s\n    ssl: false\n\n# redisson 配置\nredisson:\n  # redis key前缀\n  keyPrefix:\n  # 线程池数量\n  threads: 4\n  # Netty线程池数量\n  nettyThreads: 8\n  # 单节点配置\n  singleServerConfig:\n    # 客户端名称\n    clientName: ${spring.application.name}\n    # 最小空闲连接数\n    connectionMinimumIdleSize: 8\n    # 连接池大小\n    connectionPoolSize: 32\n    # 连接空闲超时，单位：毫秒\n    idleConnectionTimeout: 10000\n    # 命令等待超时，单位：毫秒\n    timeout: 3000\n    # 发布和订阅连接池大小\n    subscriptionConnectionPoolSize: 50\n  # redisson 缓存配置\n  cacheGroup:\n    # 用例: @Cacheable(cacheNames=\"groupId\", key=\"#XXX\") 方可使用缓存组配置\n    - groupId: redissonCacheMap\n      # 组过期时间(脚本监控)\n      ttl: 60000\n      # 组最大空闲时间(脚本监控)\n      maxIdleTime: 60000\n      # 组最大长度\n      maxSize: 0\n\n# 分布式锁 lock4j 全局配置\nlock4j:\n  # 获取分布式锁超时时间，默认为 3000 毫秒\n  acquire-timeout: 3000\n  # 分布式锁的超时时间，默认为 30 毫秒\n  expire: 30000\n\n# 暴露监控端点\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \'*\'\n  endpoint:\n    health:\n      show-details: ALWAYS\n    logfile:\n      external-file: ./logs/${spring.application.name}/console.log\n\n# 日志配置\nlogging:\n  level:\n    org.springframework: warn\n    org.apache.dubbo: warn\n    com.alibaba.nacos: warn\n  config: classpath:logback.xml\n\n# Sa-Token配置\nsa-token:\n  # token名称 (同时也是cookie名称)\n  token-name: Authorization\n  # token有效期 设为一天 (必定过期) 单位: 秒\n  timeout: 86400\n  # token临时有效期 (指定时间无操作就过期) 单位: 秒\n  activity-timeout: 1800\n  # 开启内网服务调用鉴权\n  check-id-token: true\n  # Id-Token的有效期 (单位: 秒)\n  id-token-timeout: 600\n  # 是否允许同一账号并发登录 (为true时允许一起登录, 为false时新登录挤掉旧登录)\n  is-concurrent: true\n  # 在多人登录同一账号时，是否共用一个token (为true时所有登录共用一个token, 为false时每次登录新建一个token)\n  is-share: false\n  # 是否尝试从header里读取token\n  is-read-head: true\n  # 是否尝试从cookie里读取token\n  is-read-cookie: false\n  # token前缀\n  token-prefix: \"Bearer\"\n  # jwt秘钥\n  jwt-secret-key: abcdefghijklmnopqrstuvwxyz\n  # 是否输出操作日志\n  is-log: true\n\n# MyBatisPlus配置\n# https://baomidou.com/config/\nmybatis-plus:\n  # 不支持多包, 如有需要可在注解配置 或 提升扫包等级\n  # 例如 com.**.**.mapper\n  mapperPackage: com.erahub.**.mapper\n  # 对应的 XML 文件位置\n  mapperLocations: classpath*:mapper/**/*Mapper.xml\n  # 实体扫描，多个package用逗号或者分号分隔\n  typeAliasesPackage: com.erahub.**.domain\n  # 启动时是否检查 MyBatis XML 文件的存在，默认不检查\n  checkConfigLocation: false\n  configuration:\n    # 自动驼峰命名规则（camel case）映射\n    mapUnderscoreToCamelCase: true\n    # MyBatis 自动映射策略\n    # NONE：不启用 PARTIAL：只对非嵌套 resultMap 自动映射 FULL：对所有 resultMap 自动映射\n    autoMappingBehavior: PARTIAL\n    # MyBatis 自动映射时未知列或未知属性处理策\n    # NONE：不做处理 WARNING：打印相关警告 FAILING：抛出异常和详细信息\n    autoMappingUnknownColumnBehavior: NONE\n    # 更详细的日志输出 会有性能损耗 org.apache.ibatis.logging.stdout.StdOutImpl\n    # 关闭日志记录 (可单纯使用 p6spy 分析) org.apache.ibatis.logging.nologging.NoLoggingImpl\n    # 默认日志输出 org.apache.ibatis.logging.slf4j.Slf4jImpl\n    logImpl: org.apache.ibatis.logging.nologging.NoLoggingImpl\n  global-config:\n    # 是否打印 Logo banner\n    banner: true\n    dbConfig:\n      # 主键类型\n      # AUTO 自增 NONE 空 INPUT 用户输入 ASSIGN_ID 雪花 ASSIGN_UUID 唯一 UUID\n      idType: ASSIGN_ID\n      # 逻辑已删除值\n      logicDeleteValue: 2\n      # 逻辑未删除值\n      logicNotDeleteValue: 0\n      insertStrategy: NOT_NULL\n      updateStrategy: NOT_NULL\n      where-strategy: NOT_NULL\n\n# swagger配置\nswagger:\n  # 是否开启swagger\n  enabled: true\n  info:\n    # 标题\n    title: \'标题：erahub-Cloud-Plus微服务权限管理系统_接口文档\'\n    # 描述\n    description: \'描述：微服务权限管理系统, 具体包括XXX,XXX模块...\'\n    # 版本\n    version: \'版本号：系统版本...\'\n    # 作者信息\n    contact:\n      name: Lion Li\n      email: crazylionli@163.com\n      url: https://gitee.com/JavaLionLi/erahub-Cloud-Plus\n  components:\n    # 鉴权方式配置\n    security-schemes:\n      apiKey:\n        type: APIKEY\n        in: HEADER\n        name: ${sa-token.token-name}\n  # 服务文档路径映射 参考 gateway router 配置\n  # 默认为服务名去除前缀转换为path 此处填特殊的配置\n  service-mapping:\n    erahub-gen: /code\n\n# seata配置\nseata:\n  # 默认关闭，如需启用spring.datasource.dynami.seata需要同时开启\n  enabled: true\n  # Seata 应用编号，默认为 ${spring.application.name}\n  application-id: ${spring.application.name}\n  # Seata 事务组编号，用于 TC 集群名\n  tx-service-group: ${spring.application.name}-group\n  config:\n    type: nacos\n    nacos:\n      server-addr: ${spring.cloud.nacos.server-addr}\n      group: ${spring.cloud.nacos.config.group}\n      namespace: ${spring.profiles.active}\n      data-id: seata-server.properties\n  registry:\n    type: nacos\n    nacos:\n      application: erahub-seata-server\n      server-addr: ${spring.cloud.nacos.server-addr}\n      group: ${spring.cloud.nacos.discovery.group}\n      namespace: ${spring.profiles.active}\n', 'fa40c49dbed40a02e05a5810d9da18f1', '2022-09-12 08:35:11', '2022-09-12 00:35:12', NULL, '127.0.0.1', 'D', 'dev', '');
INSERT INTO `his_config_info` VALUES (2, 67, 'datasource.yml', 'DEFAULT_GROUP', '', 'datasource:\n  system-master:\n    # jdbc 所有参数配置参考 https://lionli.blog.csdn.net/article/details/122018562\n    # rewriteBatchedStatements=true 批处理优化 大幅提升批量插入更新删除性能\n    url: jdbc:mysql://localhost:3306/erahub-cloud?useUnicode=true&characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=true&serverTimezone=GMT%2B8&rewriteBatchedStatements=true\n    username: root\n    password: 123456\n  gen:\n    url: jdbc:mysql://localhost:3306/erahub-cloud?useUnicode=true&characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=true&serverTimezone=GMT%2B8&rewriteBatchedStatements=true\n    username: root\n    password: 123456\n  job:\n    url: jdbc:mysql://localhost:3306/erahub-job?useUnicode=true&characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=true&serverTimezone=GMT%2B8&rewriteBatchedStatements=true\n    username: root\n    password: 123456\n\nspring:\n  datasource:\n    type: com.alibaba.druid.pool.DruidDataSource\n    # 动态数据源文档 https://www.kancloud.cn/tracy5546/dynamic-datasource/content\n    dynamic:\n      # 性能分析插件(有性能损耗 不建议生产环境使用)\n      p6spy: true\n      # 开启seata代理，开启后默认每个数据源都代理，如果某个不需要代理可单独关闭\n      seata: true\n      # 严格模式 匹配不到数据源则报错\n      strict: true\n      druid:\n        initial-size: 5\n        min-idle: 5\n        maxActive: 20\n        maxWait: 60000\n        timeBetweenEvictionRunsMillis: 60000\n        minEvictableIdleTimeMillis: 300000\n        validationQuery: SELECT 1 FROM DUAL\n        testWhileIdle: true\n        testOnBorrow: false\n        testOnReturn: false\n        poolPreparedStatements: true\n        maxPoolPreparedStatementPerConnectionSize: 20\n        filters: stat,slf4j\n        connectionProperties: druid.stat.mergeSql\\=true;druid.stat.slowSqlMillis\\=5000\n\n# seata配置\nseata:\n  # 关闭自动代理\n  enable-auto-data-source-proxy: false\n', 'e1cd16ee7d13f117a30d2111b0ec5fd0', '2022-09-12 08:35:11', '2022-09-12 00:35:12', NULL, '127.0.0.1', 'D', 'dev', '');
INSERT INTO `his_config_info` VALUES (3, 68, 'ruoyi-gateway.yml', 'DEFAULT_GROUP', '', '# 安全配置\nsecurity:\n  # 验证码\n  captcha:\n    # 是否开启验证码\n    enabled: true\n    # 验证码类型 math 数组计算 char 字符验证\n    type: MATH\n    # line 线段干扰 circle 圆圈干扰 shear 扭曲干扰\n    category: CIRCLE\n    # 数字验证码位数\n    numberLength: 1\n    # 字符验证码长度\n    charLength: 4\n  # 防止XSS攻击\n  xss:\n    enabled: true\n    excludeUrls:\n      - /system/notice\n  # 不校验白名单\n  ignore:\n    whites:\n      - /code\n      - /auth/logout\n      - /auth/login\n      - /auth/smsLogin\n      - /auth/xcxLogin\n      - /auth/register\n      - /resource/sms/code\n      - /*/v3/api-docs\n      - /csrf\n\nspring:\n  cloud:\n    # 网关配置\n    gateway:\n      # 打印请求日志(自定义)\n      requestLog: true\n      discovery:\n        locator:\n          lowerCaseServiceId: true\n          enabled: true\n      routes:\n        # 认证中心\n        - id: ruoyi-auth\n          uri: lb://ruoyi-auth\n          predicates:\n            - Path=/auth/**\n          filters:\n            # 验证码处理\n            - ValidateCodeFilter\n            - StripPrefix=1\n        # 代码生成\n        - id: ruoyi-gen\n          uri: lb://ruoyi-gen\n          predicates:\n            - Path=/code/**\n          filters:\n            - StripPrefix=1\n        # 系统模块\n        - id: ruoyi-system\n          uri: lb://ruoyi-system\n          predicates:\n            - Path=/system/**\n          filters:\n            - StripPrefix=1\n        # 资源服务\n        - id: ruoyi-resource\n          uri: lb://ruoyi-resource\n          predicates:\n            - Path=/resource/**\n          filters:\n            - StripPrefix=1\n        # 演示服务\n        - id: ruoyi-demo\n          uri: lb://ruoyi-demo\n          predicates:\n            - Path=/demo/**\n          filters:\n            - StripPrefix=1\n        # MQ演示服务\n        - id: ruoyi-stream-mq\n          uri: lb://ruoyi-stream-mq\n          predicates:\n            - Path=/stream-mq/**\n          filters:\n            - StripPrefix=1\n\n    # sentinel 配置\n    sentinel:\n      # nacos配置持久化\n      datasource:\n        ds1:\n          nacos:\n            server-addr: ${spring.cloud.nacos.server-addr}\n            dataId: sentinel-${spring.application.name}.json\n            groupId: ${spring.cloud.nacos.config.group}\n            namespace: ${spring.profiles.active}\n            data-type: json\n            rule-type: flow\n', 'c28731ac6c96a4a1b742355ee71c8476', '2022-09-12 08:35:11', '2022-09-12 00:35:12', NULL, '127.0.0.1', 'D', 'dev', '');
INSERT INTO `his_config_info` VALUES (4, 69, 'ruoyi-auth.yml', 'DEFAULT_GROUP', '', '# 用户配置\nuser:\n  password:\n    # 密码最大错误次数\n    maxRetryCount: 5\n    # 密码锁定时间（默认10分钟）\n    lockTime: 10\n', '1fd08de287662e81f0ced8808d7b19e6', '2022-09-12 08:35:11', '2022-09-12 00:35:12', NULL, '127.0.0.1', 'D', 'dev', '');
INSERT INTO `his_config_info` VALUES (5, 70, 'ruoyi-monitor.yml', 'DEFAULT_GROUP', '', '# 监控中心配置\nspring:\n  security:\n    user:\n      name: ruoyi\n      password: 123456\n  boot:\n    admin:\n      ui:\n        title: RuoYi-Cloud-Plus服务监控中心\n      discovery:\n        # seata 不具有健康检查的能力 防止报错排除掉\n        ignored-services: ruoyi-seata-server\n', 'ba7fa6a6bfde3dcb3b566fd8864834af', '2022-09-12 08:35:11', '2022-09-12 00:35:12', NULL, '127.0.0.1', 'D', 'dev', '');
INSERT INTO `his_config_info` VALUES (6, 71, 'ruoyi-system.yml', 'DEFAULT_GROUP', '', 'spring:\n  datasource:\n    dynamic:\n      # 设置默认的数据源或者数据源组,默认值即为 master\n      primary: master\n      datasource:\n        # 主库数据源\n        master:\n          driver-class-name: com.mysql.cj.jdbc.Driver\n          url: ${datasource.system-master.url}\n          username: ${datasource.system-master.username}\n          password: ${datasource.system-master.password}\n        # 从库数据源\n        # slave:\n        #  driver-class-name:\n        #  url:\n        #  username:\n        #  password:\n', '568773caedfdb639db5fd807aad3993d', '2022-09-12 08:35:11', '2022-09-12 00:35:12', NULL, '127.0.0.1', 'D', 'dev', '');
INSERT INTO `his_config_info` VALUES (7, 72, 'ruoyi-gen.yml', 'DEFAULT_GROUP', '', 'spring:\n  datasource:\n    dynamic:\n      # 设置默认的数据源或者数据源组,默认值即为 master\n      primary: master\n      datasource:\n        # 主库数据源\n        master:\n          driver-class-name: com.mysql.cj.jdbc.Driver\n          url: ${datasource.system-master.url}\n          username: ${datasource.system-master.username}\n          password: ${datasource.system-master.password}\n        # 从库数据源\n        #slave:\n        #  lazy: true\n        #  driver-class-name:\n        #  url:\n        #  username:\n        #  password:\n\n# 代码生成\ngen:\n  # 作者\n  author: ruoyi\n  # 默认生成包路径 system 需改成自己的模块名称 如 system monitor tool\n  packageName: com.ruoyi.system\n  # 自动去除表前缀，默认是false\n  autoRemovePre: false\n  # 表前缀（生成类名不会包含表前缀，多个用逗号分隔）\n  tablePrefix: sys_\n', '4a910788dcd9e831f5bde9b6f389d36e', '2022-09-12 08:35:11', '2022-09-12 00:35:12', NULL, '127.0.0.1', 'D', 'dev', '');
INSERT INTO `his_config_info` VALUES (8, 73, 'ruoyi-job.yml', 'DEFAULT_GROUP', '', 'spring:\n  datasource:\n    dynamic:\n      # 设置默认的数据源或者数据源组,默认值即为 master\n      primary: master\n      datasource:\n        # 主库数据源\n        master:\n          driver-class-name: com.mysql.cj.jdbc.Driver\n          url: ${datasource.job.url}\n          username: ${datasource.job.username}\n          password: ${datasource.job.password}\n\nxxl:\n  job:\n    # 执行器开关\n    enabled: true\n    # 调度中心地址：如调度中心集群部署存在多个地址则用逗号分隔。\n    admin-addresses: http://localhost:9900\n    # 执行器通讯TOKEN：非空时启用\n    access-token: xxl-job\n    # 执行器配置\n    executor:\n      # 执行器AppName：执行器心跳注册分组依据；为空则关闭自动注册\n      appname: ${spring.application.name}-executor\n      # 执行器端口号 执行器从19901开始往后写\n      port: 9901\n      # 执行器注册：默认IP:PORT\n      address:\n      # 执行器IP：默认自动获取IP\n      ip:\n      # 执行器运行日志文件存储磁盘路径\n      logpath: ./logs/${spring.application.name}/xxl-job\n      # 执行器日志文件保存天数：大于3生效\n      logretentiondays: 30\n', '3c951a136dbc9989b9184f05e08b5961', '2022-09-12 08:35:11', '2022-09-12 00:35:12', NULL, '127.0.0.1', 'D', 'dev', '');
INSERT INTO `his_config_info` VALUES (9, 74, 'ruoyi-resource.yml', 'DEFAULT_GROUP', '', 'spring:\n  datasource:\n    dynamic:\n      # 设置默认的数据源或者数据源组,默认值即为 master\n      primary: master\n      datasource:\n        # 主库数据源\n        master:\n          driver-class-name: com.mysql.cj.jdbc.Driver\n          url: ${datasource.system-master.url}\n          username: ${datasource.system-master.username}\n          password: ${datasource.system-master.password}\n        # 从库数据源\n        # slave:\n        #  driver-class-name:\n        #  url:\n        #  username:\n        #  password:\n\nmail:\n  enabled: false\n  host: smtp.163.com\n  port: 465\n  # 是否需要用户名密码验证\n  auth: true\n  # 发送方，遵循RFC-822标准\n  from: xxx@163.com\n  # 用户名（注意：如果使用foxmail邮箱，此处user为qq号）\n  user: xxx@163.com\n  # 密码（注意，某些邮箱需要为SMTP服务单独设置密码，详情查看相关帮助）\n  pass: xxxxxxxxxx\n  # 使用 STARTTLS安全连接，STARTTLS是对纯文本通信协议的扩展。\n  starttlsEnable: true\n  # 使用SSL安全连接\n  sslEnable: true\n  # SMTP超时时长，单位毫秒，缺省值不超时\n  timeout: 0\n  # Socket连接超时值，单位毫秒，缺省值不超时\n  connectionTimeout: 0\n\nsms:\n  enabled: false\n  # 阿里云 dysmsapi.aliyuncs.com\n  # 腾讯云 sms.tencentcloudapi.com\n  endpoint: \"dysmsapi.aliyuncs.com\"\n  accessKeyId: xxxxxxx\n  accessKeySecret: xxxxxx\n  signName: 测试\n  # 腾讯专用\n  sdkAppId:\n', '9356ff04b686a3cc692059758424e14e', '2022-09-12 08:35:11', '2022-09-12 00:35:12', NULL, '127.0.0.1', 'D', 'dev', '');
