INSERT INTO `his_config_info` VALUES (170, 186, 'datasource.yml', 'DEFAULT_GROUP', '', 'datasource:\r\n  system-master:\r\n    # jdbc 所有参数配置参考 https://lionli.blog.csdn.net/article/details/122018562\r\n    # rewriteBatchedStatements=true 批处理优化 大幅提升批量插入更新删除性能\r\n    url: jdbc:mysql://localhost:3306/erahub-cloud?useUnicode=true&characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=true&serverTimezone=GMT%2B8&rewriteBatchedStatements=true\r\n    username: root\r\n    password: 123456\r\n  gen:\r\n    url: jdbc:mysql://localhost:3306/erahub-cloud?useUnicode=true&characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=true&serverTimezone=GMT%2B8&rewriteBatchedStatements=true\r\n    username: root\r\n    password: 123456\r\n  job:\r\n    url: jdbc:mysql://localhost:3306/erahub-job?useUnicode=true&characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=true&serverTimezone=GMT%2B8&rewriteBatchedStatements=true\r\n    username: root\r\n    password: 123456\r\n\r\nspring:\r\n  datasource:\r\n    type: com.alibaba.druid.pool.DruidDataSource\r\n    # 动态数据源文档 https://www.kancloud.cn/tracy5546/dynamic-datasource/content\r\n    dynamic:\r\n      # 性能分析插件(有性能损耗 不建议生产环境使用)\r\n      p6spy: true\r\n      # 开启seata代理，开启后默认每个数据源都代理，如果某个不需要代理可单独关闭\r\n      seata: true\r\n      # 严格模式 匹配不到数据源则报错\r\n      strict: true\r\n      druid:\r\n        initial-size: 5\r\n        min-idle: 5\r\n        maxActive: 20\r\n        maxWait: 60000\r\n        timeBetweenEvictionRunsMillis: 60000\r\n        minEvictableIdleTimeMillis: 300000\r\n        validationQuery: SELECT 1 FROM DUAL\r\n        testWhileIdle: true\r\n        testOnBorrow: false\r\n        testOnReturn: false\r\n        poolPreparedStatements: true\r\n        maxPoolPreparedStatementPerConnectionSize: 20\r\n        filters: stat,slf4j\r\n        connectionProperties: druid.stat.mergeSql\\=true;druid.stat.slowSqlMillis\\=5000\r\n\r\n# seata配置\r\nseata:\r\n  # 关闭自动代理\r\n  enable-auto-data-source-proxy: false\r\n', '29d24e5829ecf3ec837121fe5e3331dd', '2022-09-14 20:10:32', '2022-09-14 12:10:32', 'nacos', '0:0:0:0:0:0:0:1', 'U', 'dev', '');
INSERT INTO `his_config_info` VALUES (172, 187, 'erahub-gateway.yml', 'DEFAULT_GROUP', '', '# 安全配置\r\nsecurity:\r\n  # 验证码\r\n  captcha:\r\n    # 是否开启验证码\r\n    enabled: true\r\n    # 验证码类型 math 数组计算 char 字符验证\r\n    type: MATH\r\n    # line 线段干扰 circle 圆圈干扰 shear 扭曲干扰\r\n    category: CIRCLE\r\n    # 数字验证码位数\r\n    numberLength: 1\r\n    # 字符验证码长度\r\n    charLength: 4\r\n  # 防止XSS攻击\r\n  xss:\r\n    enabled: true\r\n    excludeUrls:\r\n      - /system/notice\r\n  # 不校验白名单\r\n  ignore:\r\n    whites:\r\n      - /code\r\n      - /auth/logout\r\n      - /auth/login\r\n      - /auth/smsLogin\r\n      - /auth/xcxLogin\r\n      - /auth/register\r\n      - /resource/sms/code\r\n      - /*/v3/api-docs\r\n      - /csrf\r\n\r\nspring:\r\n  cloud:\r\n    # 网关配置\r\n    gateway:\r\n      # 打印请求日志(自定义)\r\n      requestLog: true\r\n      discovery:\r\n        locator:\r\n          lowerCaseServiceId: true\r\n          enabled: true\r\n      routes:\r\n        # 认证中心\r\n        - id: erahub-auth\r\n          uri: lb://erahub-auth\r\n          predicates:\r\n            - Path=/auth/**\r\n          filters:\r\n            # 验证码处理\r\n            - ValidateCodeFilter\r\n            - StripPrefix=1\r\n        # 代码生成\r\n        - id: erahub-gen\r\n          uri: lb://erahub-gen\r\n          predicates:\r\n            - Path=/code/**\r\n          filters:\r\n            - StripPrefix=1\r\n        # 系统模块\r\n        - id: erahub-system\r\n          uri: lb://erahub-system\r\n          predicates:\r\n            - Path=/system/**\r\n          filters:\r\n            - StripPrefix=1\r\n        # 资源服务\r\n        - id: erahub-resource\r\n          uri: lb://erahub-resource\r\n          predicates:\r\n            - Path=/resource/**\r\n          filters:\r\n            - StripPrefix=1\r\n        # 演示服务\r\n        - id: erahub-demo\r\n          uri: lb://erahub-demo\r\n          predicates:\r\n            - Path=/demo/**\r\n          filters:\r\n            - StripPrefix=1\r\n        # MQ演示服务\r