INSERT INTO `his_config_info` VALUES (182, 213, 'application-common.yml', 'DEFAULT_GROUP', '', 'server:\r\n  # undertow 配置\r\n  undertow:\r\n    # HTTP post内容的最大大小。当值为-1时，默认值为大小是无限的\r\n    max-http-post-size: -1\r\n    # 以下的配置会影响buffer,这些buffer会用于服务器连接的IO操作,有点类似netty的池化内存管理\r\n    # 每块buffer的空间大小,越小的空间被利用越充分\r\n    buffer-size: 512\r\n    # 是否分配的直接内存\r\n    direct-buffers: true\r\n    threads:\r\n      # 设置IO线程数, 它主要执行非阻塞的任务,它们会负责多个连接, 默认设置每个CPU核心一个线程\r\n      io: 8\r\n      # 阻塞任务线程池, 当执行类似servlet请求阻塞操作, undertow会从这个线程池中取得线程,它的值设置取决于系统的负载\r\n      worker: 256\r\n\r\ndubbo:\r\n  application:\r\n    logger: slf4j\r\n    # 元数据中心 local 本地 remote 远程 这里使用远程便于其他服务获取\r\n    metadataType: remote\r\n    # 可选值 interface、instance、all，默认是 all，即接口级地址、应用级地址都注册\r\n    register-mode: instance\r\n    service-discovery:\r\n      # FORCE_INTERFACE，只消费接口级地址，如无地址则报错，单订阅 2.x 地址\r\n      # APPLICATION_FIRST，智能决策接口级/应用级地址，双订阅\r\n      # FORCE_APPLICATION，只消费应用级地址，如无地址则报错，单订阅 3.x 地址\r\n      migration: FORCE_APPLICATION\r\n  protocol:\r\n    # 设置为 tri 即可使用 Triple 3.0 新协议\r\n    # 性能对比 dubbo 协议并没有提升 但基于 http2 用于多语言异构等 http 交互场景\r\n    # 使用 dubbo 协议通信\r\n    name: dubbo\r\n    # dubbo 协议端口(-1表示自增端口,从20880开始)\r\n    port: -1\r\n    # 指定dubbo协议注册ip\r\n    # host: 192.168.0.100\r\n  # 注册中心配置\r\n  registry:\r\n    address: nacos://${spring.cloud.nacos.server-addr}\r\n    group: DUBBO_GROUP\r\n    parameters:\r\n      namespace: ${spring.profiles.active}\r\n  # 消费者相关配置\r\n  consumer:\r\n    # 结果缓存(LRU算法)\r\n    # 会有数据不一致问题 建议在注解局部开启\r\n    cache: false\r\n    # 支持校验注解\r\n    validation: true\r\n    # 超时时间\r\n    timeout: 3000\r\n    # 初始化检查\r\n    check: false\r\n  scan:\r\n    # 接口实现类扫描\r\n    base-packages: com.erahub.**.dubbo\r\n  # 自定义配置\r\n  custom:\r\n    # 全局请求log\r\n    request-log: true\r\n    # info 基础信息 param 参数信息 full 全部\r\n    log-level: info\r\n\r\nspring:\r\n  # 资源信息\r\n  messages:\r\n    # 国际化资源文件路径\r\n    basename: i18n/messages\r\n  servlet:\r\n    multipart:\r\n      # 整个请求大小限制\r\n      max-request-size: 20MB\r\n      # 上传单个文件大小限制\r\n      max-file-size: 10MB\r\n  #jackson配置\r\n  jackson:\r\n    # 日期格式化\r\n    date-format: yyyy-MM-dd HH:mm:ss\r\n    serialization:\r\n      # 格式化输出\r\n      INDENT_OUTPUT: false\r\n      # 忽略无法转换的对象\r\n      fail_on_empty_beans: false\r\n    deserialization:\r\n      # 允许对象忽略json中不存在的属性\r\n      fail_on_unknown_properties: false\r\n  cloud:\r\n    # sentinel 配置\r\n    sentinel:\r\n      # 取消控制台懒加载\r\n      eager: true\r\n      transport:\r\n        # 控制台地址\r\n        dashboard: localhost:8718\r\n\r\n  # redis通用配置 子服务可以自行配置进行覆盖\r\n  redis:\r\n    host: localhost\r\n    port: 6379\r\n    # 密码(如没有密码请注释掉)\r\n    #password:\r\n    database: 0\r\n    timeout: 10s\r\n    ssl: false\r\n\r\n# redisson 配置\r\nredisson:\r\n  # redis key前缀\r\n  keyPrefix:\r\n  # 线程池数量\r\n  threads: 4\r\n  # Netty线程池数量\r\n  nettyThreads: 8\r\n  # 单节点配置\r\n  singleServerConfig:\r\n    # 客户端名称\r\n    clientName: ${spring.application.name}\r\n    # 最小空闲连接数\r\n    connectionMinimumIdleSize: 8\r\n    # 连接池大小\r\n    connectionPoolSize: 32\r\n    # 连接空闲超时，单位：毫秒\r\n    idleConnectionTimeout: 10000\r\n    # 命令等待超时，单位：毫秒\r\n    timeout: 3000\r\n    # 发布和订阅连接池大小\r\n    subscriptionConnectionPoolSize: 50\r\n  # redisson 缓存配置\r\n  cacheGroup:\r\n    # 用例: @Cacheable(cacheNames=\"groupId\", key=\"#XXX\") 方可使用缓存组配置\r\n    - groupId: redissonCacheMap\r\n      # 组过期时间(脚本监控)\r\n      ttl: 60000\r\n      # 组最大空闲时间(脚本监控)\r\n      maxIdleTime: 60000\r\n      # 组最大长度\r\n      maxSize: 0\r\n\r\n# 分布式锁 lock4j 全局配置\r\nlock4j:\r\n  # 获取分布式锁超时时间，默认为 3000 毫秒\r\n  acquire-timeout: 3000\r\n  # 分布式锁的超时时间，默认为 30 毫秒\r\n  expire: 30000\r\n\r\n# 暴露监控端点\r\nmanagement:\r\n  endpoints:\r\n    web:\r\n      exposure:\r\n        include: \'*\'\r\n  endpoint:\r\n    health:\r\n      show-details: ALWAYS\r\n    logfile:\r\n      external-file: ./logs/${spring.application.name}/console.log\r\n\r\n# 日志配置\r\nlogging:\r\n  level:\r\n    org.springframework: warn\r\n    org.apache.dubbo: warn\r\n    com.alibaba.nacos: warn\r\n  config: classpath:logback.xml\r\n\r\n# Sa-Token配置\r\nsa-token:\r\n  # token名称 (同时也是cookie名称)\r\n  token-name: Authorization\r\n  # token有效期 设为一天 (必定过期) 单位: 秒\r\n  timeout: 86400\r\n  # token临时有效期 (指定时间无操作就过期) 单位: 秒\r\n  activity-timeout: 1800\r\n  # 开启内网服务调用鉴权\r\n  check-id-token: true\r\n  # Id-Token的有效期 (单位: 秒)\r\n  id-token-timeout: 600\r\n  # 是否允许同一账号并发登录 (为true时允许一起登录, 为false时新登录挤掉旧登录)\r\n  is-concurrent: true\r\n  # 在多人登录同一账号时，是否共用一个token (为true时所有登录共用一个token, 为false时每次登录新建一个token)\r\n  is-share: false\r\n  # 是否尝试从header里读取token\r\n  is-read-head: true\r\n  # 是否尝试从cookie里读取token\r\n  is-read-cookie: false\r\n  # token前缀\r\n  token-prefix: \"Bearer\"\r\n  # jwt秘钥\r\n  jwt-secret-key: abcdefghijklmnopqrstuvwxyz\r\n  # 是否输出操作日志\r\n  is-log: true\r\n\r\n# MyBatisPlus配置\r\n# https://baomidou.com/config/\r\nmybatis-plus:\r\n  # 不支持多包, 如有需要可在注解配置 或 提升扫包等级\r\n  # 例如 com.**.**.mapper\r\n  mapperPackage: com.erahub.**.mapper\r\n  # 对应的 XML 文件位置\r\n  mapperLocations: classpath*:mapper/**/*Mapper.xml\r\n  # 实体扫描，多个package用逗号或者分号分隔\r\n  typeAliasesPackage: com.erahub.**.domain\r\n  # 启动时是否检查 MyBatis XML 文件的存在，默认不检查\r\n  checkConfigLocation: false\r\n  configuration:\r\n    # 自动驼峰命名规则（camel case）映射\r\n    mapUnderscoreToCamelCase: true\r\n    # MyBatis 自动映射策略\r\n    # NONE：不启用 PARTIAL：只对非嵌套 resultMap 自动映射 FULL：对所有 resultMap 自动映射\r\n    autoMappingBehavior: PARTIAL\r\n    # MyBatis 自动映射时未知列或未知属性处理策\r\n    # NONE：不做处理 WARNING：打印相关警告 FAILING：抛出异常和详细信息\r\n    autoMappingUnknownColumnBehavior: NONE\r\n    # 更详细的日志输出 会有性能损耗 org.apache.ibatis.logging.stdout.StdOutImpl\r\n    # 关闭日志记录 (可单纯使用 p6spy 分析) org.apache.ibatis.logging.nologging.NoLoggingImpl\r\n    # 默认日志输出 org.apache.ibatis.logging.slf4j.Slf4jImpl\r\n    logImpl: org.apache.ibatis.logging.nologging.NoLoggingImpl\r\n  global-config:\r\n    # 是否打印 Logo banner\r\n    banner: true\r\n    dbConfig:\r\n      # 主键类型\r\n      # AUTO 自增 NONE 空 INPUT 用户输入 ASSIGN_ID 雪花 ASSIGN_UUID 唯一 UUID\r\n      idType: ASSIGN_ID\r\n      # 逻辑已删除值\r\n      logicDeleteValue: 2\r\n      # 逻辑未删除值\r\n      logicNotDeleteValue: 0\r\n      insertStrategy: NOT_NULL\r\n      updateStrategy: NOT_NULL\r\n      where-strategy: NOT_NULL\r\n\r\n# swagger配置\r\nswagger:\r\n  # 是否开启swagger\r\n  enabled: true\r\n  info:\r\n    # 标题\r\n    title: \'标题：erahub-Cloud-Plus微服务权限管理系统_接口文档\'\r\n    # 描述\r\n    description: \'描述：微服务权限管理系统, 具体包括XXX,XXX模块...\'\r\n    # 版本\r\n    version: \'版本号：系统版本...\'\r\n    # 作者信息\r\n    contact:\r\n      name: Lion Li\r\n      email: crazylionli@163.com\r\n      url: https://gitee.com/JavaLionLi/erahub-Cloud-Plus\r\n  components:\r\n    # 鉴权方式配置\r\n    security-schemes:\r\n      apiKey:\r\n        type: APIKEY\r\n        in: HEADER\r\n        name: ${sa-token.token-name}\r\n  # 服务文档路径映射 参考 gateway router 配置\r\n  # 默认为服务名去除前缀转换为path 此处填特殊的配置\r\n  service-mapping:\r\n    erahub-gen: /code\r\n\r\n# seata配置\r\nseata:\r\n  # 默认关闭，如需启用spring.datasource.dynami.seata需要同时开启\r\n  enabled: true\r\n  # Seata 应用编号，默认为 ${spring.application.name}\r\n  application-id: ${spring.application.name}\r\n  # Seata 事务组编号，用于 TC 集群名\r\n  tx-service-group: ${spring.application.name}-group\r\n  config:\r\n    type: nacos\r\n    nacos:\r\n      server-addr: ${spring.cloud.nacos.server-addr}\r\n      group: ${spring.cloud.nacos.config.group}\r\n      namespace: ${spring.profiles.active}\r\n      data-id: seata-server.properties\r\n  registry:\r\n    type: nacos\r\n    nacos:\r\n      application: erahub-seata-server\r\n      server-addr: ${spring.cloud.nacos.server-addr}\r\n      group: ${spring.cloud.nacos.discovery.group}\r\n      namespace: ${spring.profiles.active}\r\n', '5f60abc4eedd5124af3ecffd58941a21', '2022-09-16 18:57:16', '2022-09-16 10:57:17', 'nacos', '127.0.0.1', 'U', 'prod', '');
INSERT INTO `his_config_info` VALUES (169, 214, 'application-common.yml', 'DEFAULT_GROUP', '', 'server:\r\n  # undertow 配置\r\n  undertow:\r\n    # HTTP post内容的最大大小。当值为-1时，默认值为大小是无限的\r\n    max-http-post-size: -1\r\n    # 以下的配置会影响buffer,这些buffer会用于服务器连接的IO操作,有点类似netty的池化内存管理\r\n    # 每块buffer的空间大小,越小的空间被利用越充分\r\n    buffer-size: 512\r\n    # 是否分配的直接内存\r\n    direct-buffers: true\r\n    threads:\r\n      # 设置IO线程数, 它主要执行非阻塞的任务,它们会负责多个连接, 默认设置每个CPU核心一个线程\r\n      io: 8\r\n      # 阻塞任务线程池, 当执行类似servlet请求阻塞操作, undertow会从这个线程池中取得线程,它的值设置取决于系统的负载\r\n      worker: 256\r\n\r\ndubbo:\r\n  application:\r\n    logger: slf4j\r\n    # 元数据中心 local 本地 remote 远程 这里使用远程便于其他服务获取\r\n    metadataType: remote\r\n    # 可选值 interface、instance、all，默认是 all，即接口级地址、应用级地址都注册\r\n    register-mode: instance\r\n    service-discovery:\r\n      # FORCE_INTERFACE，只消费接口级地址，如无地址则报错，单订阅 2.x 地址\r\n      # APPLICATION_FIRST，智能决策接口级/应用级地址，双订阅\r\n      # FORCE_APPLICATION，只消费应用级地址，如无地址则报错，单订阅 3.x 地址\r\n      migration: FORCE_APPLICATION\r\n  protocol:\r\n    # 设置为 tri 即可使用 Triple 3.0 新协议\r\n    # 性能对比 dubbo 协议并没有提升 但基于 http2 用于多语言异构等 http 交互场景\r\n    # 使用 dubbo 协议通信\r\n    name: dubbo\r\n    # dubbo 协议端口(-1表示自增端口,从20880开始)\r\n    port: -1\r\n    # 指定dubbo协议注册ip\r\n    # host: 192.168.0.100\r\n  # 注册中心配置\r\n  registry:\r\n    address: nacos://${spring.cloud.nacos.server-addr}\r\n    group: DUBBO_GROUP\r\n    parameters:\r\n      namespace: ${spring.profiles.active}\r\n  # 消费者相关配置\r\n  consumer:\r\n    # 结果缓存(LRU算法)\r\n    # 会有数据不一致问题 建议在注解局部开启\r\n    cache: false\r\n    # 支持校验注解\r\n    validation: true\r\n    # 超时时间\r\n    timeout: 3000\r\n    # 初始化检查\r\n    check: false\r\n  scan:\r\n    # 接口实现类扫描\r\n    base-packages: com.erahub.**.dubbo\r\n  # 自定义配置\r\n  custom:\r\n    # 全局请求log\r\n    request-log: true\r\n    # info 基础信息 param 参数信息 full 全部\r\n    log-level: info\r\n\r\nspring:\r\n  # 资源信息\r\n  messages:\r\n    # 国际化资源文件路径\r\n    basename: i18n/messages\r\n  servlet:\r\n    multipart:\r\n      # 整个请求大小限制\r\n      max-request-size: 20MB\r\n      # 上传单个文件大小限制\r\n      max-file-size: 10MB\r\n  #jackson配置\r\n  jackson:\r\n    # 日期格式化\r\n    date-format: yyyy-MM-dd HH:mm:ss\r\n    serialization:\r\n      # 格式化输出\r\n      INDENT_OUTPUT: false\r\n      # 忽略无法转换的对象\r\n      fail_on_empty_beans: false\r\n    deserialization:\r\n      # 允许对象忽略json中不存在的属性\r\n      fail_on_unknown_properties: false\r\n  cloud:\r\n    # sentinel 配置\r\n    sentinel:\r\n      # 取消控制台懒加载\r\n      eager: true\r\n      transport:\r\n        # 控制台地址\r\n        dashboard: localhost:8718\r\n\r\n  # redis通用配置 子服务可以自行配置进行覆盖\r\n  redis:\r\n    host: localhost\r\n    port: 6379\r\n    # 密码(如没有密码请注释掉)\r\n    #password:\r\n    database: 0\r\n    timeout: 10s\r\n    ssl: false\r\n\r\n# redisson 配置\r\nredisson:\r\n  # redis key前缀\r\n  keyPrefix:\r\n  # 线程池数量\r\n  threads: 4\r\n  # Netty线程池数量\r\n  nettyThreads: 8\r\n  # 单节点配置\r\n  singleServerConfig:\r\n    # 客户端名称\r\n    clientName: ${spring.application.name}\r\n    # 最小空闲连接数\r\n    connectionMinimumIdleSize: 8\r\n    # 连接池大小\r\n    connectionPoolSize: 32\r\n    # 连接空闲超时，单位：毫秒\r\n    idleConnectionTimeout: 10000\r\n    # 命令等待超时，单位：毫秒\r\n    timeout: 3000\r\n    # 发布和订阅连接池大小\r\n    subscriptionConnectionPoolSize: 50\r\n  # redisson 缓存配置\r\n  cacheGroup:\r\n    # 用例: @Cacheable(cacheNames=\"groupId\", key=\"#XXX\") 方可使用缓存组配置\r\n    - groupId: redissonCacheMap\r\n      # 组过期时间(脚本监控)\r\n      ttl: 60000\r\n      # 组最大空闲时间(脚本监控)\r\n      maxIdleTime: 60000\r\n      # 组最大长度\r\n      maxSize: 0\r\n\r\n# 分布式锁 lock4j 全局配置\r\nlock4j:\r\n  # 获取分布式锁超时时间，默认为 3000 毫秒\r\n  acquire-timeout: 3000\r\n  # 分布式锁的超时时间，默认为 30 毫秒\r\n  expire: 30000\r\n\r\n# 暴露监控端点\r\nmanagement:\r\n  endpoints:\r\n    web:\r\n      exposure:\r\n        include: \'*\'\r\n  endpoint:\r\n    health:\r\n      show-details: ALWAYS\r\n    logfile:\r\n      external-file: ./logs/${spring.application.name}/console.log\r\n\r\n# 日志配置\r\nlogging:\r\n  level:\r\n    org.springframework: warn\r\n    org.apache.dubbo: warn\r\n    com.alibaba.nacos: warn\r\n  config: classpath:logback.xml\r\n\r\n# Sa-Token配置\r\nsa-token:\r\n  # token名称 (同时也是cookie名称)\r\n  token-name: Authorization\r\n  # token有效期 设为一天 (必定过期) 单位: 秒\r\n  timeout: 86400\r\n  # token临时有效期 (指定时间无操作就过期) 单位: 秒\r\n  activity-timeout: 1800\r\n  # 开启内网服务调用鉴权\r\n  check-id-token: true\r\n  # Id-Token的有效期 (单位: 秒)\r\n  id-token-timeout: 600\r\n  # 是否允许同一账号并发登录 (为true时允许一起登录, 为false时新登录挤掉旧登录)\r\n  is-concurrent: true\r\n  # 在多人登录同一账号时，是否共用一个token (为true时所有登录共用一个token, 为false时每次登录新建一个token)\r\n  is-share: false\r\n  # 是否尝试从header里读取token\r\n  is-read-head: true\r\n  # 是否尝试从cookie里读取token\r\n  is-read-cookie: false\r\n  # token前缀\r\n  token-prefix: \"Bearer\"\r\n  # jwt秘钥\r\n  jwt-secret-key: abcdefghijklmnopqrstuvwxyz\r\n  # 是否输出操作日志\r\n  is-log: true\r\n\r\n# MyBatisPlus配置\r\n# https://baomidou.com/config/\r\nmybatis-plus:\r\n  # 不支持多包, 如有需要可在注解配置 或 提升扫包等级\r\n  # 例如 com.**.**.mapper\r\n  mapperPackage: com.erahub.**.mapper\r\n  # 对应的 XML 文件位置\r\n  mapperLocations: classpath*:mapper/**/*Mapper.xml\r\n  # 实体扫描，多个package用逗号或者分号分隔\r\n  typeAliasesPackage: com.erahub.**.domain\r\n  # 启动时是否检查 MyBatis XML 文件的存在，默认不检查\r\n  checkConfigLocation: false\r\n  configuration:\r\n    # 自动驼峰命名规则（camel case）映射\r\n    mapUnderscoreToCamelCase: true\r\n    # MyBatis 自动映射策略\r\n    # NONE：不启用 PARTIAL：只对非嵌套 resultMap 自动映射 FULL：对所有 resultMap 自动映射\r\n    autoMappingBehavior: PARTIAL\r\n    # MyBatis 自动映射时未知列或未知属性处理策\r\n    # NONE：不做处理 WARNING：打印相关警告 FAILING：抛出异常和详细信息\r\n    autoMappingUnknownColumnBehavior: NONE\r\n    # 更详细的日志输出 会有性能损耗 org.apache.ibatis.logging.stdout.StdOutImpl\r\n    # 关闭日志记录 (可单纯使用 p6spy 分析) org.apache.ibatis.logging.nologging.NoLoggingImpl\r\n    # 默认日志输出 org.apache.ibatis.logging.slf4j.Slf4jImpl\r\n    logImpl: org.apache.ibatis.logging.nologging.NoLoggingImpl\r\n  global-config:\r\n    # 是否打印 Logo banner\r\n    banner: true\r\n    dbConfig:\r\n      # 主键类型\r\n      # AUTO 自增 NONE 空 INPUT 用户输入 ASSIGN_ID 雪花 ASSIGN_UUID 唯一 UUID\r\n      idType: ASSIGN_ID\r\n      # 逻辑已删除值\r\n      logicDeleteValue: 2\r\n      # 逻辑未删除值\r\n      logicNotDeleteValue: 0\r\n      insertStrategy: NOT_NULL\r\n      updateStrategy: NOT_NULL\r\n      where-strategy: NOT_NULL\r\n\r\n# swagger配置\r\nswagger:\r\n  # 是否开启swagger\r\n  enabled: true\r\n  info:\r\n    # 标题\r\n    title: \'标题：erahub-Cloud-Plus微服务权限管理系统_接口文档\'\r\n    # 描述\r\n    description: \'描述：微服务权限管理系统, 具体包括XXX,XXX模块...\'\r\n    # 版本\r\n    version: \'版本号：系统版本...\'\r\n    # 作者信息\r\n    contact:\r\n      name: Lion Li\r\n      email: crazylionli@163.com\r\n      url: https://gitee.com/JavaLionLi/erahub-Cloud-Plus\r\n  components:\r\n    # 鉴权方式配置\r\n    security-schemes:\r\n      apiKey:\r\n        type: APIKEY\r\n        in: HEADER\r\n        name: ${sa-token.token-name}\r\n  # 服务文档路径映射 参考 gateway router 配置\r\n  # 默认为服务名去除前缀转换为path 此处填特殊的配置\r\n  service-mapping:\r\n    erahub-gen: /code\r\n\r\n# seata配置\r\nseata:\r\n  # 默认关闭，如需启用spring.datasource.dynami.seata需要同时开启\r\n  enabled: true\r\n  # Seata 应用编号，默认为 ${spring.application.name}\r\n  application-id: ${spring.application.name}\r\n  # Seata 事务组编号，用于 TC 集群名\r\n  tx-service-group: ${spring.application.name}-group\r\n  config:\r\n    type: nacos\r\n    nacos:\r\n      server-addr: ${spring.cloud.nacos.server-addr}\r\n      group: ${spring.cloud.nacos.config.group}\r\n      namespace: ${spring.profiles.active}\r\n      data-id: seata-server.properties\r\n  registry:\r\n    type: nacos\r\n    nacos:\r\n      application: erahub-seata-server\r\n      server-addr: ${spring.cloud.nacos.server-addr}\r\n      group: ${spring.cloud.nacos.discovery.group}\r\n      namespace: ${spring.profiles.active}\r\n', '5f60abc4eedd5124af3ecffd58941a21', '2022-09-16 18:57:32', '2022-09-16 10:57:33', 'nacos', '127.0.0.1', 'U', 'dev', '');
INSERT INTO `his_config_info` VALUES (183, 215, 'datasource.yml', 'DEFAULT_GROUP', '', 'datasource:\n  system-master:\n    # jdbc 所有参数配置参考 https://lionli.blog.csdn.net/article/details/122018562\n    # rewriteBatchedStatements=true 批处理优化 大幅提升批量插入更新删除性能\n    url: jdbc:mysql://localhost:3306/erahub-cloud?useUnicode=true&characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=true&serverTimezone=GMT%2B8&rewriteBatchedStatements=true\n    username: root\n    password: 123456\n  gen:\n    url: jdbc:mysql://localhost:3306/erahub-cloud?useUnicode=true&characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=true&serverTimezone=GMT%2B8&rewriteBatchedStatements=true\n    username: root\n    password: 123456\n  job:\n    url: jdbc:mysql://localhost:3306/erahub-job?useUnicode=true&characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=true&serverTimezone=GMT%2B8&rewriteBatchedStatements=true\n    username: root\n    password: 123456\n\nspring:\n  datasource:\n    type: com.alibaba.druid.pool.DruidDataSource\n    # 动态数据源文档 https://www.kancloud.cn/tracy5546/dynamic-datasource/content\n    dynamic:\n      # 性能分析插件(有性能损耗 不建议生产环境使用)\n      p6spy: true\n      # 开启seata代理，开启后默认每个数据源都代理，如果某个不需要代理可单独关闭\n      seata: true\n      # 严格模式 匹配不到数据源则报错\n      strict: true\n      druid:\n        initial-size: 5\n        min-idle: 5\n        maxActive: 20\n        maxWait: 60000\n        timeBetweenEvictionRunsMillis: 60000\n        minEvictableIdleTimeMillis: 300000\n        validationQuery: SELECT 1 FROM DUAL\n        testWhileIdle: true\n        testOnBorrow: false\n        testOnReturn: false\n        poolPreparedStatements: true\n        maxPoolPreparedStatementPerConnectionSize: 20\n        filters: stat,slf4j\n        connectionProperties: druid.stat.mergeSql\\=true;druid.stat.slowSqlMillis\\=5000\n\n# seata配置\nseata:\n  # 关闭自动代理\n  enable-auto-data-source-proxy: false\n', 'e1cd16ee7d13f117a30d2111b0ec5fd0', '2022-09-16 18:58:23', '2022-09-16 10:58:24', 'nacos', '127.0.0.1', 'U', 'prod', '');
INSERT INTO `his_config_info` VALUES (187, 216, 'erahub-job.yml', 'DEFAULT_GROUP', '', 'spring:\r\n  datasource:\r\n    dynamic:\r\n      # 设置默认的数据源或者数据源组,默认值即为 master\r\n      primary: master\r\n      datasource:\r\n        # 主库数据源\r\n        master:\r\n          driver-class-name: com.mysql.cj.jdbc.Driver\r\n          url: ${datasource.job.url}\r\n          username: ${datasource.job.username}\r\n          password: ${datasource.job.password}\r\n\r\nxxl:\r\n  job:\r\n    # 执行器开关\r\n    enabled: true\r\n    # 调度中心地址：如调度中心集群部署存在多个地址则用逗号分隔。\r\n    admin-addresses: http://localhost:9900\r\n    # 执行器通讯TOKEN：非空时启用\r\n    access-token: xxl-job\r\n    # 执行器配置\r\n    executor:\r\n      # 执行器AppName：执行器心跳注册分组依据；为空则关闭自动注册\r\n      appname: ${spring.application.name}-executor\r\n      # 执行器端口号 执行器从19901开始往后写\r\n      port: 9901\r\n      # 执行器注册：默认IP:PORT\r\n      address:\r\n      # 执行器IP：默认自动获取IP\r\n      ip:\r\n      # 执行器运行日志文件存储磁盘路径\r\n      logpath: ./logs/${spring.application.name}/xxl-job\r\n      # 执行器日志文件保存天数：大于3生效\r\n      logretentiondays: 30\r\n', 'caaba788f24a9d44ee7d59cebe01e27f', '2022-09-16 19:00:00', '2022-09-16 11:00:00', 'nacos', '127.0.0.1', 'U', 'prod', '');
INSERT INTO `his_config_info` VALUES (193, 217, 'seata-server.properties', 'DEFAULT_GROUP', '', 'service.vgroupMapping.erahub-auth-group=default\r\nservice.vgroupMapping.erahub-system-group=default\r\nservice.vgroupMapping.erahub-resource-group=default\r\nservice.vgroupMapping.erahub-gen-group=default\r\nservice.vgroupMapping.erahub-job-group=default\r\n\r\nservice.enableDegrade=false\r\nservice.disableGlobalTransaction=false\r\n\r\n#Transaction storage configuration, only for the server. The file, DB, and redis configuration values are optional.\r\nstore.mode=db\r\nstore.lock.mode=db\r\nstore.session.mode=db\r\n#Used for password encryption\r\nstore.publicKey=\r\n\r\n#These configurations are required if the `store mode` is `db`. If `store.mode,store.lock.mode,store.session.mode` are not equal to `db`, you can remove the configuration block.\r\nstore.db.datasource=druid\r\nstore.db.dbType=mysql\r\nstore.db.driverClassName=com.mysql.cj.jdbc.Driver\r\nstore.db.url=jdbc:mysql://127.0.0.1:3306/erahub-seata?useUnicode=true&rewriteBatchedStatements=true\r\nstore.db.user=root\r\nstore.db.password=123456\r\nstore.db.minConn=5\r\nstore.db.maxConn=30\r\nstore.db.globalTable=global_table\r\nstore.db.branchTable=branch_table\r\nstore.db.distributedLockTable=distributed_lock\r\nstore.db.queryLimit=100\r\nstore.db.lockTable=lock_table\r\nstore.db.maxWait=5000\r\n\r\n#Transaction rule configuration, only for the server\r\nserver.recovery.committingRetryPeriod=1000\r\nserver.recovery.asynCommittingRetryPeriod=1000\r\nserver.recovery.rollbackingRetryPeriod=1000\r\nserver.recovery.timeoutRetryPeriod=1000\r\nserver.maxCommitRetryTimeout=-1\r\nserver.maxRollbackRetryTimeout=-1\r\nserver.rollbackRetryTimeoutUnlockEnable=false\r\nserver.distributedLockExpireTime=10000\r\nserver.xaerNotaRetryTimeout=60000\r\nserver.session.branchAsyncQueueSize=5000\r\nserver.session.enableBranchAsyncRemove=false\r\n\r\n#Transaction rule configuration, only for the client\r\nclient.rm.asyncCommitBufferLim